const axios = require('axios');
const MockAdapter = require('axios-mock-adapter');

const mock = new MockAdapter(axios);

mock.onGet('/tickets').reply(200, {
  tickets: [
    {
      id: Math.random(),
      date: '16, FEB',
      startTime: '6.30',
      endTime: '8.45',
      price: '125',
      planeId: 1,
    },
    {
      id: Math.random(),
      date: '16, FEB',
      startTime: '13.35',
      endTime: '15.50',
      price: '150',
      planeId: 2,
    },
    {
      id: Math.random(),
      date: '16, FEB',
      startTime: '16.40',
      endTime: '18.55',
      price: '115',
      planeId: 3,
    },
  ],
});

//добавитб свободные места, места, (эконом/бизнес классы)

mock.onPost('/search-request').reply(() => axios.get('/tickets'));

mock.onGet('/airports').reply(200, {
  airports: [{ name: 'Belarus' },
    { name: 'Belarus' },
    { name: 'Netherlands' },
    { name: 'Poland' },
    { name: 'Ukrain' },
    { name: 'Litva' },
    { name: 'Latvia' },
    { name: 'Estonia' },
    { name: 'Russia' },
  ],
});
// добавить название аэропортов

mock.onGet('/planes').reply(200, [
  {
    rows: 15,
    location: [1, 1, 1, 0, 1, 1, 1],
  },
  {
    rows: 15,
    location: [1, 1, 0, 1, 1, 0, 1, 1],
  },
  {
    rows: 15,
    location: [1, 0, 1, 1, 1, 0, 1],
  },
]);

mock.onGet('/luggage-types').reply(200, {
  luggage: [
    {
      "kg": "10",
      "price": "9"
    },
    {
      "kg": "20",
      "price": "15"
    },
    {
      "kg": "30",
      "price": "21"
    },
    {
      "kg": "free carry-on bag",
      "price": "0"
    }
  ]
})

export default axios;
