import axios from '../../../data';
import { BESTDEALS_HAS_ERRORED, BESTDEALS_IS_LOADING, BESTDEALS_FETCH_DATA_SUCCESS } from '../actionTypes';
import { setUserRequestData } from '../../user/actions';

export function hasErrored(bool) {
  return {
    type: BESTDEALS_HAS_ERRORED,
    hasErrored: bool,
  };
}

export function isLoading(bool) {
  return {
    type: BESTDEALS_IS_LOADING,
    isLoading: bool,
  };
}

export function fetchDataSuccess(items) {
  return {
    type: BESTDEALS_FETCH_DATA_SUCCESS,
    items,
  };
}

export function bestdealsFetchData(url, userRequest) {
  return (dispatch) => {
    dispatch(isLoading(true));
    dispatch(setUserRequestData(userRequest));

    axios.post(url)
      .then((response) => {
        if (!response.data.tickets.length) {
          throw Error(response.statusText);
        }

        dispatch(isLoading(false));

        return response;
      })
      .then(response => response.data.tickets)
      .then(tickets => dispatch(fetchDataSuccess(tickets)))
      .catch(() => {
        dispatch(hasErrored(true));
      });
  };
}
