import { handleActions } from 'redux-actions';
import { TICKETS_IS_LOADING, TICKETS_HAS_ERRORED, TICKETS_FETCH_DATA_SUCCESS } from '../actionTypes';

const initialState = {
  hasErrored: false,
  isLoading: false,
  items: [],
};

function ticketState () {
  return {
    hasErrored: false,
    isLoading: false,
    items: [],
  };
}

export default function tickets(state = initialState, action) {
  switch (action.type) {
    case TICKETS_HAS_ERRORED:
      return {
        ...state,
        hasErrores: action.hasErrored,
      };
    case TICKETS_IS_LOADING:
      return {
        ...state,
        isLoading: action.isLoading,
      };
    case TICKETS_FETCH_DATA_SUCCESS:
      return {
        ...state,
        items: action.items,
      };
    default:
      return state;
  }
}


const tickets = handleActions(
  {
    [AIRPORT_FETCH_DATA_SUCCESS]: (state, action) => ({
      ...state, items: action.items
    }),
    [AIRPORT_HAS_ERRORED]: (state, action) => ({
      ...state, hasErrored: action.hasErrored
    }),
  },
  airState ()
);
