import React, { Component, Fragment } from "react";

class Seats extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isBooked: false
    };
  }

  bookSeat = () => {
    this.setState({ isBooked: !this.state.isBooked });
  };

  render() {
    const seats = [];
    console.log(this.state);
    for (let i = 1; i <= this.props.seatsAmount; i++) {
      seats.push(
        <div
          className="seat"
          id={i + this.props.thisRowLetter}
          onClick={this.bookSeat}
        >
          <div
            className={this.props.isBooked ? "label-reserved" : "label"}
            id={i + this.props.thisRowLetter}
          >
            {i + this.props.thisRowLetter}
          </div>
        </div>
      );
    }
    // console.log(this.props);
    return <Fragment>{seats}</Fragment>;
  }
}

export default Seats;
