import React, { Component, Fragment } from "react";

class Seats extends Component {
  onClickSeat(seats) {
    this.props.onClickData(seats);
  }
  render() {
    const seats = [];
    for (let i = 1; i <= this.props.seatsAmount; i++) {
      seats.push(
        <div className="seat">
          <label
            className={
              this.props.reserved.indexOf(i) > -1 ? "reserved" : "available"
            }
            onClick={e => this.onClickSeat(i)}
          >
            {i + this.props.thisRowLetter}
          </label>
        </div>
      );
    }

    return <Fragment>{seats}</Fragment>;
  }
}

export default Seats;
