import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { planeFetchData } from "../../redux/search/planeShema/actions";

import Rows from "./Rows";

import "./index.scss";

class PlaneSeats extends Component {
  constructor(props) {
    super(props);

    // this.state = {
    //   plane: {
    //     rows: 2,
    //     subRows: 3,
    //     seats: 10,
    //     bookedSeats: []
    //   }
    // };
  }

  static propTypes = {
    planeFetchData: PropTypes.func.isRequired,
    plane: PropTypes.array.isRequired
  };

  componentDidMount() {
    this.props.planeFetchData();
  }

  render() {
    const rows = [];
    for (let i = 0; i < this.state.plane.rows; i++) {
      rows.push(
        <Rows
          className={"row" + { i }}
          subRows={this.state.plane.subRows}
          seats={this.state.plane.seats}
        />
      );
    }
    console.log(rows);
    return (
      <div className="plane">
        <div class="cockpit">
          <h1>Please select seats</h1>
        </div>
        <div className="exit exit--front fuselage" />
        <div className="cabin fuselage">{rows}</div>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  plane: state.searchPage.plane.items,
  planeHasErrored: state.searchPage.plane.hasErrored,
  planeIsLoading: state.searchPage.plane.isLoading
});

const mapDispatchToProps = dispatch => ({
  planeFetchData: (url, values) => dispatch(planeFetchData(url, values))
});

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(PlaneSeats)
);
