import React, { Component } from "react";
import Seats from "./Seats";

class Rows extends Component {
  onClickData(seats) {
    if (this.props.plane.bookedSeats.indexOf(seats) > -1) {
      this.setState({
        seatAvailable: this.props.plane.seatAvailable.concat(seats),
        bookedSeats: this.props.plane.bookedSeats.filter(res => res != seats)
      });
    } else {
      this.setState({
        bookedSeats: this.props.plane.bookedSeats.concat(seats),
        seatAvailable: this.props.plane.seatAvailable.filter(
          res => res != seats
        )
      });
    }
  }

  render() {
    const subRows = [];
    for (var i = 1; i <= this.props.subRows; i++) {
      subRows.push(
        <div className="subrow">
          <Seats
            thisRowLetter={String.fromCharCode(
              this.props.startingLetter + i - 1
            )}
            seatsAmount={this.props.seats}
            available={this.state.seatAvailable}
            reserved={this.state.seatReserved}
            onClickData={this.onClickData.bind(this)}
          />
        </div>
      );
    }
    return <div className="row-plane">{subRows}</div>;
  }
}

export default Rows;
