import React, { Component, Fragment } from "react";

class Seats extends Component {
  render() {
    const seats = [];
    for (let i = 1; i <= this.props.seatsAmount; i++) {
      seats.push(
        <div className="seat">
          <input type="checkbox" />
          <label>{i}</label>
        </div>
      );
    }

    return <Fragment>{seats}</Fragment>;
  }
}

export default Seats;
