import { createSelector } from "reselect";
import moment from 'moment';

const getUserRequest = state => state.user.request;
const getTickets = state => state.searchPage.tickets.items;

const departure = this.props.userRequest;
const back = this.props.userRequest.return;

const ticketsSearchSelector = createSelector([getUserRequest, getTickets],
  (UserRequest,Tickets) => {
    const fromTickets = Tickets.filter(ticket => ticket.dateFrom === UserRequest.moment(departure).format('LL') && ticket.fromCountry === UserRequest.from);
    return fromTickets.filter(ticket2 => ticket2.dateTo === UserRequest.moment(back).format('LL') && ticket2.toCountry === UserRequest.to);
});

export default ticketsSearchSelector;

