import React from 'react';
import Typography from '@material-ui/core/Typography';
import FlightTakeoffIcon from '@material-ui/icons/FlightTakeoff';
import FlightLandIcon from '@material-ui/icons/FlightLand';
import './index.scss';
import Button from 'react-bootstrap/Button';
import Table from 'react-bootstrap/Table'

function FlightsListItems({
  classes, flights,
}) {

  return ( 
    flights.map(({
      id, date, startTime, endTime, price,
    }) => (
      <div key={id} className="flights-list-item">
        <div className="flights-list-item__info">
          <Typography variant="subtitle1" gutterBottom>
            {date}
          </Typography>
          <Typography variant="h4" gutterBottom>
            {startTime}
            <FlightTakeoffIcon className={classes.icon} fontSize="large" />
            <FlightLandIcon className={classes.icon} fontSize="large" />
            {endTime}
          </Typography>
        </div>
        <div className="flights-list-item__price">
          <Button 
            type="button"
            className="card-buttons"
          >
            $ {price}
          </Button>
        </div>
      </div>
    ))
  );
}

export default FlightsListItems;
export function BestDeals1({flights}) {
  return (
    flights.map(({dateFrom, dateTo, startTime, endTime, fromCountry, toCountry, price}) => (
      <Table responsive>
        <thead>
            <tr>
            <th>Date From</th>
            <th>Date To</th>
            <th>Start time</th>
            <th>End time</th>
            <th>From Country</th>
            <th>To Country</th>
            <th>Price</th>
            <th>Book right now ⤋</th>
            </tr>
        </thead>
        <tbody>
            <tr>
            <td>{dateFrom}</td>
            <td>{dateTo}</td>
            <td>{startTime}</td>
            <td>{endTime}</td>
            <td>{fromCountry}</td>
            <td>{toCountry}</td>
            <td>{price}</td>
            <td><Button className="card-buttons">BOOK NOW</Button></td>
            </tr>
        </tbody>
        </Table>
    )
    ) 
  );
}
